import React, { useState } from 'react';
import {
  Container,
  Grid,
  Paper,
  Typography,
  Button,
  Box,
  TextField,
  List,
  ListItem,
  ListItemText,
  ListItemAvatar,
  Avatar,
  Divider,
  Card,
  CardContent,
  CardActions,
  Chip,
  Tabs,
  Tab
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

// Mock data for forum posts
const mockPosts = [
  {
    id: 1,
    author: 'Juan Pérez',
    title: 'Duda sobre Programación I',
    content: '¿Alguien puede explicarme el concepto de recursividad?',
    category: 'Programación',
    date: '2024-03-15',
    replies: [
      {
        id: 1,
        author: 'María López',
        content: 'La recursividad es cuando una función se llama a sí misma...',
        date: '2024-03-15'
      }
    ]
  },
  {
    id: 2,
    author: 'Carlos Gómez',
    title: 'Material de estudio para Matemáticas',
    content: '¿Dónde puedo encontrar material adicional para estudiar cálculo?',
    category: 'Matemáticas',
    date: '2024-03-14',
    replies: []
  }
];

function Foro() {
  const { user } = useAuth();
  const [posts, setPosts] = useState(mockPosts);
  const [newPost, setNewPost] = useState({ title: '', content: '', category: '' });
  const [selectedPost, setSelectedPost] = useState(null);
  const [reply, setReply] = useState('');
  const navigate = useNavigate();

  // Tabs
  const tabValue = 5; // FORO ACADÉMICO
  const handleTabChange = (e, newValue) => {
    if (newValue === 0) navigate('/inicio');
    else if (newValue === 1) navigate('/solicitudes');
    else if (newValue === 2) navigate('/solicitudes');
    else if (newValue === 3) navigate('/solicitudes');
    else if (newValue === 4) navigate('/solicitudes');
    else if (newValue === 5) navigate('/foro');
  };

  const handleNewPost = () => {
    const post = {
      id: posts.length + 1,
      author: user?.name || 'Anónimo',
      ...newPost,
      date: new Date().toISOString().split('T')[0],
      replies: []
    };
    setPosts([post, ...posts]);
    setNewPost({ title: '', content: '', category: '' });
  };

  const handleReply = (postId) => {
    if (!reply.trim()) return;

    const updatedPosts = posts.map(post => {
      if (post.id === postId) {
        return {
          ...post,
          replies: [
            ...post.replies,
            {
              id: post.replies.length + 1,
              author: user?.name || 'Anónimo',
              content: reply,
              date: new Date().toISOString().split('T')[0]
            }
          ]
        };
      }
      return post;
    });

    setPosts(updatedPosts);
    setReply('');
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      {/* Barra de tabs superior */}
      <Box sx={{ display: 'flex', alignItems: 'center', p: 2, pb: 0, position: 'relative' }}>
        <img
          src={process.env.PUBLIC_URL + '/logo-udem.png'}
          alt="Logo UdeM"
          style={{ height: 40, marginRight: 24 }}
        />
        <Tabs
          value={tabValue}
          onChange={handleTabChange}
          textColor="inherit"
          TabIndicatorProps={{ style: { background: '#000' } }}
          sx={{
            bgcolor: 'white',
            borderRadius: 2,
            minHeight: 48,
            '.MuiTab-root': { color: '#a10f1a', fontWeight: 600 },
            '.Mui-selected': { color: '#000' },
            ml: 2
          }}
        >
          <Tab label="INICIO" />
          <Tab label="ACADÉMICAS" />
          <Tab label="FINANCIERAS" />
          <Tab label="ADMINISTRATIVAS" />
          <Tab label="PQRS" />
          <Tab label="Foro Académico" />
        </Tabs>
      </Box>
      <Grid container spacing={3}>
        {/* Header */}
        <Grid item xs={12}>
          <Paper sx={{ p: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <Typography variant="h4" component="h1">
              Foro Académico
            </Typography>
          </Paper>
        </Grid>

        {/* New Post Form */}
        <Grid item xs={12}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Nueva Publicación
              </Typography>
              <Grid container spacing={2}>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="Título"
                    value={newPost.title}
                    onChange={(e) => setNewPost({ ...newPost, title: e.target.value })}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    multiline
                    rows={4}
                    label="Contenido"
                    value={newPost.content}
                    onChange={(e) => setNewPost({ ...newPost, content: e.target.value })}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="Categoría"
                    value={newPost.category}
                    onChange={(e) => setNewPost({ ...newPost, category: e.target.value })}
                  />
                </Grid>
              </Grid>
            </CardContent>
            <CardActions>
              <Button
                variant="contained"
                color="primary"
                onClick={handleNewPost}
                disabled={!newPost.title || !newPost.content || !newPost.category}
              >
                Publicar
              </Button>
            </CardActions>
          </Card>
        </Grid>

        {/* Posts List */}
        <Grid item xs={12}>
          <List>
            {posts.map((post) => (
              <React.Fragment key={post.id}>
                <ListItem alignItems="flex-start">
                  <ListItemAvatar>
                    <Avatar>{post.author[0]}</Avatar>
                  </ListItemAvatar>
                  <ListItemText
                    primary={
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <Typography variant="h6">{post.title}</Typography>
                        <Chip label={post.category} size="small" />
                      </Box>
                    }
                    secondary={
                      <>
                        <Typography component="span" variant="body2" color="text.primary">
                          {post.author}
                        </Typography>
                        {` — ${post.content}`}
                        <Typography variant="caption" display="block" sx={{ mt: 1 }}>
                          {post.date}
                        </Typography>
                      </>
                    }
                  />
                </ListItem>

                {/* Replies */}
                {post.replies.map((reply) => (
                  <ListItem key={reply.id} sx={{ pl: 9 }}>
                    <ListItemAvatar>
                      <Avatar>{reply.author[0]}</Avatar>
                    </ListItemAvatar>
                    <ListItemText
                      primary={reply.author}
                      secondary={
                        <>
                          {reply.content}
                          <Typography variant="caption" display="block" sx={{ mt: 1 }}>
                            {reply.date}
                          </Typography>
                        </>
                      }
                    />
                  </ListItem>
                ))}

                {/* Reply Form */}
                <ListItem sx={{ pl: 9 }}>
                  <Box sx={{ width: '100%', display: 'flex', gap: 1 }}>
                    <TextField
                      fullWidth
                      size="small"
                      label="Escribir respuesta"
                      value={reply}
                      onChange={(e) => setReply(e.target.value)}
                    />
                    <Button
                      variant="contained"
                      onClick={() => handleReply(post.id)}
                      disabled={!reply.trim()}
                    >
                      Responder
                    </Button>
                  </Box>
                </ListItem>
                <Divider variant="inset" component="li" />
              </React.Fragment>
            ))}
          </List>
        </Grid>
      </Grid>
    </Container>
  );
}

export default Foro;
